function shuffle(arr) {
    for (var i = arr.length - 1; i > 0; i = 0 | i - 1) {
        var r = Math.floor(Math.random() * (i + 1));
        var tmp = arr[i];
        arr[i] = arr[r];
        arr[r] = tmp;
    }
}

